import React, { Component } from 'react';
// import * as data from './fetch.js'


class CriminalList extends Component {
    state = { categories: [], crimes: [] }

    componentDidMount() {
        console.log('mount')
        fetch('https://data.police.uk/api/crime-categories?date=2011-11').then(res => res.json()).then((categories) => {
            return (this.setState(() => {
                return { categories }
            }));
        })
        fetch('https://data.police.uk/api/crime-categories?date=2011-11').then(res => res.json()).then((crimes) => {
            return (this.setState(() => {
                return { crimes }
            }));
        })

    }




    render() {
        console.log('render')
        const { categories } = this.state;
        const { crimes } = this.state;
        return (<ul>

            {categories.map(({ name }) => {
                return (<>
                    <li> {name}</li>

                </>);
            })} </ul>)

    }
}

export default CriminalList;
