import React, { Component } from 'react';
import * as data from './fetch.js'


class CriminalList extends Component {
    state = { categories: [], crimes: [] }

    componentDidMount() {
        console.log('mount')
        data.fetchData.then((crime) => {
            return (this.setState(() => {
                return { crime }
            }));
        })
    }


    render() {
        console.log('render')
        return (<ul>

            {this.state.categories.map(({ name }) => {
                return <li> {name}</li>
            })}
        </ul>);
    }
}

export default CriminalList;
