import React, { Component } from 'react';
import * as data from './fetch.js'


class CriminalList extends Component {
    state = { list: [] }

    componentDidMount() {
        console.log('mount')
        data.fetchData().then(({ name }) => {
            this.setState({ list: name })
        })
    }

    render() {
        return (<ul>

            {this.state.list.map(name => {
                return <li> {name}</li>
            })}
        </ul>);
    }
}

export default CriminalList;
